# https://leetcode.com/problems/maximum-length-of-repeated-subarray/
# 718. Maximum Length of Repeated Subarray
# leet_718
from typing import List


class Solution:
    # https://en.wikipedia.org/wiki/Longest_common_substring_problem
    def findLength(self, nums1: List[int], nums2: List[int]) -> int:
        r = len(nums1)
        n = len(nums2)
        result = 0
        line = [0] * r
        line_pre = [0] * r
        
        for i in range(r):
            for j in range(n):
                if nums1[i] == nums2[j]:
                    if i and j:  # не крайние строки с начала
                        line[j] = line_pre[j - 1] + 1
                    else:
                        line[j] = 1
                    
                    result = max(result, line[j])
                else:
                    line[j] = 0
            line_pre = line.copy()

        return result


sol = Solution()

print(sol.findLength(nums1 = [1,2,3,2,1], nums2 = [3,2,1,4,7]))
print(sol.findLength(nums1 = [0,0,0,0,0], nums2 = [0,0,0,0,0]))
print(sol.findLength(nums1 = [0,0,0,0,1], nums2 = [1,0,0,0,0]))
